function ResourceListWithInstaPost(props) {

    const resourceName = {
        singular: 'Posts',
        plural: 'Posts',
    };

    const items = props.instapost;


    return (
        <Card>
            <ResourceList
                resourceName={resourceName}
                items={items}
                renderItem={renderItem}
            />
        </Card>
    );

    function renderItem(item) {
        const {id,media_url} = item;
        const media = <Avatar customer size="medium" src={media_url} />;

        return (
            <ResourceItem
                id={id}
                name={media_url}

                url= {`instagram?page_id=${id}&access_token=${media_url}`}
            >
                <h3>
                    <TextStyle variation="strong">{media_url}</TextStyle>
                </h3>
                <div>{media_url}</div>
            </ResourceItem>
        );
    }
}


class Instagram extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            allpost : ''
        }
    }

    componentDidMount(){

        const search = window.location.search; // could be '?foo=bar'
        const params = new URLSearchParams(search);

        const page_id = params.get('page_id');
        const page_access_token = params.get('access_token');


        var alldata = {
            page_id: page_id,
            page_access_token: page_access_token
        }

        var self = this;
        axios.post('http://localhost:5000/get_page_detail/',alldata)
            .then(function (response1) {
                self.setState({
                    allpost : response1.data
                })
            })
            .catch(function (error) {
                console.log(error);
            });


    }

    render() {

        let instagrampost;

        console.log(this.state.allpost);
        if(this.state.allpost!=''){
            instagrampost = (
                <ResourceListWithInstaPost instapost={this.state.allpost.data} />
            );
        }

        return <div>{instagrampost}</div>;
    }


}

export default Instagram;
