class Facebook extends React.Component {


    constructor(props) {
        super(props);

        this.state = {
            pages: props.alldata.data,
        };

    }


    render() {

        let fbpagecontent;

        if(this.state.pages!=''){
            fbpagecontent = (
                    <FacebookPageList pages={this.state.pages} />
            )
        }

        return <Page><div className='fb_page_model'>{fbpagecontent}</div></Page>;
    }
}


export default Facebook;
