import React, {useCallback, useState} from 'react';
import {Card, Tabs} from '@shopify/polaris';

function FittedTabsExample() {
    const [selected, setSelected] = useState(0);

    const handleTabChange = useCallback(
        (selectedTabIndex) => setSelected(selectedTabIndex),
        [],
    );

    const tabs = [
        {
            id: 'all-gallary-setting',
            content: 'Gallary Settings',
            accessibilityLabel: 'Gallary Settings',
            panelID: 'all-gallary-setting-fitted-Ccontent',
        },
        {
            id: 'all-accepts-information',
            content: 'Information1',
            accessibilityLabel: 'Information',
            panelID: 'all-accepts-information-fitted-Ccontent',
        },
        {
            id: 'all-accepts-appereance',
            content: 'Appereance1',
            accessibilityLabel: 'Appereance',
            panelID: 'all-accepts-appereance-fitted-Ccontent',
        },
        {
            id: 'all-accepts-shop',
            content: 'Shop1',
            accessibilityLabel: 'Shop',
            panelID: 'all-accepts-shop-fitted-Ccontent',
        }
    ];

        const tabPanels = [
            {
                content :  <Tabs.Panel id="all-gallary-setting-fitted-Ccontent">
                    something 111
                </Tabs.Panel>
            },
            {
                content :   <Tabs.Panel id="all-accepts-information-fitted-Ccontent">
                    something 222
                </Tabs.Panel>
            },
            {
                content :  <Tabs.Panel id="all-accepts-appereance-fitted-Ccontent">
                    something 333
                </Tabs.Panel>
            },
            {
                content :   <Tabs.Panel id="all-accepts-shop-fitted-Ccontent">
                    something 444
                </Tabs.Panel>
            }
        ];



    return (
        <Card>
            <Tabs tabs={tabs} selected={selected} onSelect={handleTabChange} fitted>
                <Card.Section title={tabs[selected].accessibilityLabel}>
                    {tabPanels[selected]}
                    <p>Tab {selected} selected</p>
                    <p>Tab {selected} {tabs[selected].content}</p>
                </Card.Section>
            </Tabs>
        </Card>
    );
}


class Instasettings extends React.Component {
    constructor(props) {
        super(props);

    }


    render() {

        return <div>
            <FittedTabsExample />
        </div>;
    }


}

export default Instasettings;
