import React, {useCallback, useState} from 'react';
import {Button, Checkbox, Form, FormLayout,SettingToggle,TextStyle, TextField,Select} from '@shopify/polaris';


class Postsettings extends React.Component {
    constructor(props) {
        super(props);

    }


    render() {

        return <div>
            <FormOnSubmitExample />
        </div>;
    }


}

export default Postsettings;


function FormOnSubmitExample() {
    const [newsletter, setNewsletter] = useState(false);
    const [email, setEmail] = useState('');

    const handleSubmit = useCallback((_event) => {
        setEmail('sdd');
        setNewsletter(false);
    }, []);

    const handleNewsLetterChange = useCallback(
        (value) => setNewsletter(value),
        [],
    );
    const [selected, setSelected] = useState('grid');
    const [selected1, setSelected1] = useState('zoom_out');
    const handleSelectChange = useCallback((value) => setSelected(value), []);
    const handleSelectChange1 = useCallback((value) => setSelected1(value), []);

    const handleEmailChange = useCallback((value) => setEmail(value), []);

    const options = [
        {label: 'Grid', value: 'grid'},
        {label: 'List', value: 'list'}
    ];

    const options1 = [
        {label: 'Zoom Out', value: 'zoom_out'},
        {label: 'Zoom In', value: 'zoom_in'}
    ];

    const [active, setActive] = useState(false);

    const handleToggle = useCallback(() => setActive((active) => !active), []);

    const contentStatus = active ? 'Disable' : 'Enable';
    const textStatus = active ? 'enabled' : 'disabled';


    return (
        <Form onSubmit={handleSubmit}>
            <FormLayout>
            <FormLayout.Group>

                <Select
                    label="Widget Type"
                    options={options}
                    onChange={handleSelectChange}
                    value={selected}
                />

                <Select
                    label="Hover Effect"
                    options={options1}
                    onChange={handleSelectChange1}
                    value={selected1}
                />

                <SettingToggle
                    action={{
                        content: contentStatus,
                        onAction: handleToggle,
                    }}
                    enabled={active}
                >
                    This setting is <TextStyle variation="strong">{textStatus}</TextStyle>.
                </SettingToggle>

            </FormLayout.Group>
                <Button submit>Submit</Button>
            </FormLayout>
        </Form>
    );
}
