import React, {useCallback, useState} from 'react';
import {Card, Tabs} from '@shopify/polaris';

function FittedTabsExample() {
    const [selected, setSelected] = useState(0);

    const handleTabChange = useCallback(
        (selectedTabIndex) => setSelected(selectedTabIndex),
        [],
    );

    const tabs = [
        {
            id: 'all-customers-fitted',
            content: 'All1',
            accessibilityLabel: 'All customers',
            panelID: 'all-customers-fitted-content',
        },
        {
            id: 'accepts-marketing-fitted',
            content: 'Accepts marketing1',
            panelID: 'accepts-marketing-fitted-Ccontent',
        },
    ];

    return (
        <Card>
            <Tabs tabs={tabs} selected={selected} onSelect={handleTabChange} fitted>
                <Card.Section title={tabs[selected].content}>
                    <p>Tab {selected} selected</p>
                </Card.Section>
            </Tabs>
        </Card>
    );
}


class Instasettings extends React.Component {
    constructor(props) {
        super(props);

    }


    render() {

        return <div>
            <FittedTabsExample />
        </div>;
    }


}

export default Instasettings;
