import React from 'react';
import {Button, Heading, Form, FormLayout,SettingToggle} from '@shopify/polaris';

import "@shopify/polaris/styles.css";



class Acceptinformation extends React.Component {
    constructor(props) {
        super(props);


        this.state = {
            display_border : false,
            display_border_button_name : 'Enable',
            social_sharing : false,
            social_sharing_button_name : 'Enable',
        }


        this.handleToggle = this.handleToggle.bind(this);
        this.handleToggleSocial = this.handleToggleSocial.bind(this);

    }


    handleToggle(){

        if(this.state.display_border == false) {
            this.setState({
                display_border : true,
                display_border_button_name : 'Disable'
            })
        }else{
            this.setState({
                display_border : false,
                display_border_button_name : 'Enable'
            })
        }

    }

    handleToggle(){

        if(this.state.display_border == false) {
            this.setState({
                display_border : true,
                display_border_button_name : 'Disable'
            })
        }else{
            this.setState({
                display_border : false,
                display_border_button_name : 'Enable'
            })
        }

    }

    handleToggle(){

        if(this.state.display_border == false) {
            this.setState({
                display_border : true,
                display_border_button_name : 'Disable'
            })
        }else{
            this.setState({
                display_border : false,
                display_border_button_name : 'Enable'
            })
        }

    }

    handleToggleSocial(){

        if(this.state.social_sharing == false) {
            this.setState({
                social_sharing : true,
                social_sharing_button_name : 'Disable'
            })
        }else{
            this.setState({
                social_sharing : false,
                social_sharing_button_name : 'Enable'
            })
        }

    }


    render() {


        return <div>
            <Form
                // onSubmit={handleSubmit}
            >
                <FormLayout>
                    <Heading>Information</Heading>
                    <FormLayout.Group>

                        <SettingToggle
                            action={{
                                content: this.state.display_border_button_name,
                                onAction: this.handleInfoLike,
                            }}
                            enabled={this.state.display_border}
                            >Like Count</SettingToggle>

                        <SettingToggle
                            action={{
                                content: this.state.display_border_button_name,
                                onAction: this.handleInfoComment,
                            }}
                            enabled={this.state.display_border}>Comment Count</SettingToggle>
                        <SettingToggle
                            action={{
                                content: this.state.display_border_button_name,
                                onAction: this.handleInfoDescription,
                            }}
                            enabled={this.state.display_border}>Description</SettingToggle>


                    </FormLayout.Group>

                    <Heading>Popup</Heading>

                    <FormLayout.Group>
                        <SettingToggle
                            action={{
                                content: this.state.social_sharing_button_name,
                                onAction: this.handleToggleSocial,
                            }}
                            enabled={this.state.social_sharing}>Like Count</SettingToggle>
                        <SettingToggle
                            action={{
                                content: this.state.social_sharing_button_name,
                                onAction: this.handleToggleSocial,
                            }}
                            enabled={this.state.social_sharing}>Comment Count</SettingToggle>
                        <SettingToggle
                            action={{
                                content: this.state.social_sharing_button_name,
                                onAction: this.handleToggleSocial,
                            }}
                            enabled={this.state.social_sharing}>Description</SettingToggle>

                    </FormLayout.Group>

                    <Button submit>Submit</Button>
                </FormLayout>
            </Form>
        </div>;
    }


}

export default Acceptinformation;
