import React, {useCallback, useState} from 'react';
import {Card, Tabs} from '@shopify/polaris';
import Postsettings from '../pages/postsettings';
import Acceptinformation from '../pages/acceptsinformation';

function FittedTabsExample() {
    const [selected, setSelected] = useState(0);

    const handleTabChange = useCallback(
        (selectedTabIndex) => setSelected(selectedTabIndex),
        [],
    );

    const tabs = [
        {
            id: 'all-post-setting',
            content: 'Gallary Settings',
            accessibilityLabel: 'Gallary Settings',
            panelID: 'all-post-setting-fitted-Ccontent',
        },
        {
            id: 'all-accepts-information',
            content: 'Information',
            accessibilityLabel: 'Information',
            panelID: 'all-accepts-information-fitted-Ccontent',
        },
        {
            id: 'all-accepts-appereance',
            content: 'Appereance',
            accessibilityLabel: 'Appereance',
            panelID: 'all-accepts-appereance-fitted-Ccontent',
        },
        {
            id: 'all-accepts-shop',
            content: 'Shop',
            accessibilityLabel: 'Shop',
            panelID: 'all-accepts-shop-fitted-Ccontent',
        }
    ];

    let tabcontent;
    if(selected==0){
        tabcontent = (
            <Postsettings />
        );
    }else if(selected==1){
        tabcontent = (
            <Acceptinformation />
        );
    }

    return (
        <Card>
            <Tabs tabs={tabs} selected={selected} onSelect={handleTabChange} fitted>
                <Card.Section>
                    {tabcontent}
                </Card.Section>
            </Tabs>
        </Card>
    );
}


class Instasettings extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            allsettings : false
        }

    }

    componentDidMount(){
        const search = window.location.search; // could be '?foo=bar'
        const params = new URLSearchParams(search);
        const shop_name = params.get('shop');

        const postdata = {
            shop : shop_name,
        }

        var self = this;
        axios.post('http://localhost:5000/get_settings/',postdata)
            .then(function (response1) {
                if(response1.data!=false){
                    self.setnewstatus(response1.data);
                }
            })
            .catch(function (error) {
                console.log(error);
            });
