import React from 'react';
import {AppProvider,Button, Checkbox, Form, FormLayout,SettingToggle,TextStyle, TextField,Select,Card,Page,Popover,ColorPicker,Stack,hsbToRgb,rgbToHsb,rgbString} from '@shopify/polaris';

import "@shopify/polaris/styles.css";



class Acceptinformation extends React.Component {
    constructor(props) {
        super(props);


        this.state = {
            color: {
                hue: 300,
                brightness: 1,
                saturation: 0.7,
                alpha: 0.8

            },
            popoverActive: false,
            checkwidget : 'grid',
            checkhover : 'zoom_out',
            display_border : true,
            display_border_button_name : 'Enable',
            social_sharing : true,
            social_sharing_button_name : 'Enable',
            border_width_value : 1,
            widget_heading_value: '',
            layout_column_value : 6,
            layout_row_value : 2,
            padding_value : 1
        }


        this.handleToggle = this.handleToggle.bind(this);
        this.handleToggleSocial = this.handleToggleSocial.bind(this);

    }


    handleToggle(){

        if(this.state.display_border == false) {
            this.setState({
                display_border : true,
                display_border_button_name : 'Disable'
            })
        }else{
            this.setState({
                display_border : false,
                display_border_button_name : 'Enable'
            })
        }

    }

    handleToggleSocial(){

        if(this.state.social_sharing == false) {
            this.setState({
                social_sharing : true,
                social_sharing_button_name : 'Disable'
            })
        }else{
            this.setState({
                social_sharing : false,
                social_sharing_button_name : 'Enable'
            })
        }

    }


    render() {


        const { color, popoverActive } = this.state;

        const rgbaColor = rgbString(hsbToRgb(color));

        const activator = (
            <Button onClick={this.handlePopoverOpen}>
                <Stack alignment="center" spacing="tight">
                    <div
                        style={{
                            height: "2rem",
                            width: "2rem",
                            borderRadius: "0.3rem",
                            background: rgbaColor
                        }}
                    />
                    <span>Border color</span>
                </Stack>
            </Button>
        );


        const options = [
            {label: 'Grid', value: 'grid'},
            {label: 'List', value: 'list'}
        ];

        const options1 = [
            {label: 'Zoom Out', value: 'zoom_out'},
            {label: 'Zoom In', value: 'zoom_in'}
        ];


        return <div>
            <Form
                // onSubmit={handleSubmit}
            >
                <FormLayout>
                    <FormLayout.Group>

                        <SettingToggle
                            action={{
                                content: this.state.display_border_button_name,
                                onAction: this.handleToggle,
                            }}
                            enabled={this.state.display_border}
                        />
                        <SettingToggle
                            action={{
                                content: this.state.display_border_button_name,
                                onAction: this.handleToggle,
                            }}
                            enabled={this.state.display_border}
                        />
                        <SettingToggle
                            action={{
                                content: this.state.display_border_button_name,
                                onAction: this.handleToggle,
                            }}
                            enabled={this.state.display_border}
                        />


                    </FormLayout.Group>
                    <FormLayout.Group>
                        <SettingToggle
                            action={{
                                content: this.state.social_sharing_button_name,
                                onAction: this.handleToggleSocial,
                            }}
                            enabled={this.state.social_sharing}
                        />
                        <SettingToggle
                            action={{
                                content: this.state.social_sharing_button_name,
                                onAction: this.handleToggleSocial,
                            }}
                            enabled={this.state.social_sharing}
                        />
                        <SettingToggle
                            action={{
                                content: this.state.social_sharing_button_name,
                                onAction: this.handleToggleSocial,
                            }}
                            enabled={this.state.social_sharing}
                        />

                    </FormLayout.Group>

                    <Button submit>Submit</Button>
                </FormLayout>
            </Form>
        </div>;
    }


}

export default Acceptinformation;
