import axios from 'axios';
import React, { useState ,useCallback} from 'react';
import {Page,Button,Modal, Card,Layout,Stack, ResourceItem,Scrollable, TextStyle} from '@shopify/polaris';


function InstaPostList(props) {

    const items = props.instapost;

    const [active, setActive] = useState(true);

    const handleChange = useCallback(() => setActive(!active), [active]);


    const handleScrollBottom = useCallback(() => alert('Scrolled to bottom'), []);

    const pageclass = "fb_page_model_button";

    return (
        <div style={{height: '50px'}}>

            <div className={pageclass}>
                <Button onClick={handleChange}>Open</Button>
            </div>

            <Modal
                large
                open={active}
                title="Instagram Post"
                onScrolledToBottom={handleScrollBottom}
                className='my-own-class'
            >
                <Layout>
                {items.map((value, index) => {

                    return  <Modal.Section key={index} className='my-own-class'>
                        <Stack vertical oneThird>
                        <img
                            alt=""
                            width="100%"
                            height="100%"
                            style={{
                                objectFit: 'cover',
                                objectPosition: 'center',
                            }}
                            src={value.media_url} />
                        </Stack>
                    </Modal.Section>

                })}

                </Layout>
            </Modal>
        </div>
    );
}

function ResourceListWithInstaPost(props) {


    const items = props.instapost;



    return (
        <Card>
             <Layout>
                 {items.map((value, index) => {
                     return <Layout.Section oneThird><img
                         alt=""
                         width="100%"
                         height="100%"
                         style={{
                             objectFit: 'cover',
                             objectPosition: 'center',
                         }}
                         src={value.media_url} /></Layout.Section>
                 })}
             </Layout>

        </Card>
    );

}


class Instagram extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            allpost : ''
        }
    }

    componentDidMount(){

        const search = window.location.search; // could be '?foo=bar'
        const params = new URLSearchParams(search);

        const page_id = params.get('page_id');
        const page_access_token = params.get('access_token');


        var alldata = {
            page_id: page_id,
            page_access_token: page_access_token
        }

        var self = this;
        axios.post('http://localhost:5000/get_page_detail/',alldata)
            .then(function (response1) {
                self.setState({
                    allpost : response1.data
                })
            })
            .catch(function (error) {
                console.log(error);
            });


    }

    render() {

        let instagrampost;

        console.log(this.state.allpost);
        if(this.state.allpost!=''){
            instagrampost = (
                <InstaPostList instapost={this.state.allpost.data} />
            );
        }

        return <div>{instagrampost}</div>;
    }


}

export default Instagram;
