import React from 'react';
import axios from 'axios';
import FacebookLogin from 'react-facebook-login';
import FacebookPage from '../pages/facebookpages';


class Index extends React.Component {

    constructor(props) {

        console.log(props);

        super(props);
        this.state = {
            isFBLoggedIn : false,
            userID:'',
            name:'',
            email:'',
            picture:'',
            allpages:''
        };

        this.logoutFacebook = this.logoutFacebook.bind(this);

    }

    componentDidMount(){


        const search = window.location.search; // could be '?foo=bar'
        const params = new URLSearchParams(search);
        const shop_name = params.get('shop');
        console.log(shop_name);
        // var shopname = this.shopname();
        // console.log('1111');
        // console.log(shopname);
        // console.log('2222');
        //
        //
        const postdata = {
            shop : shop_name
        }
        //
        var self= this;

        axios.post('http://localhost:5000/check_login_account/',postdata)
            .then(function (response1) {

                self.changestatus(response1);
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    changestatus(response){

        this.setState({
            isFBLoggedIn : true,
            userID:response1.userID,
            name:response1.name,
            email:response1.email,
            picture:response1.picture.data.url,
            allpages : response.data
        })
    }

    logoutFacebook(){
        console.log('111');
        this.setState({
            isFBLoggedIn : false
        })
    }

    setstatus(response,response1) {
        this.setState({
            isFBLoggedIn : true,
            userID:response1.userID,
            name:response1.name,
            email:response1.email,
            picture:response1.picture.data.url,
            allpages : response.data
        })
    }


       responseFacebook = (response) => {

            console.log(response);
            if(response.data!='undefined'){
                var self= this;

                const search = window.location.search; // could be '?foo=bar'
                const params = new URLSearchParams(search);
                const shop_name = params.get('shop');

                const postdata = {
                    shop : shop_name,
                    response : response
                }

                axios.post('http://localhost:5000/save_accesstoken/',postdata)
                    .then(function (response1) {
                        console.log(response1);
                        const shopdata = {
                            shop : shop_name
                        }

                        axios.post('http://localhost:5000/get_fb_pages/',shopdata)
                            .then(function (response1) {
                                console.log(response1);
                            })
                            .catch(function (error) {
                                console.log(error);
                            });

                        self.setstatus(response1,response);
                    })
                    .catch(function (error) {
                        console.log(error);
                    });
            }

       }


    componentClicked = () => console.log('clieked');

    render() {


        let fbContent;

        if(this.state.isFBLoggedIn){
            fbContent = (
                <div>
                    <FacebookPage alldata={this.state.allpages} />
                </div>
            );
        }else{
            fbContent = (<FacebookLogin
                appId="591251971460198"
                autoLoad = {true}
                fields="name,email,picture"
                scope="public_profile,email,manage_pages,read_insights,pages_show_list,instagram_basic"
                onClick={this.componentClicked}
                callback={this.responseFacebook}
            />);
        }

        return <div className="ankit">{fbContent}</div>;
    }
}



export default Index;
