class Facebook extends React.Component {


    constructor(props) {
        super(props);

        this.state = {
            pages: props.alldata.data,
            allpost : '',
            getpost : false
        }
        this.openModal = this.openModal.bind(this);
    }

    openModal(response){
        console.log(response);
        this.setState({
            getpost : true,
            allpost : response.data
        });
    }


    render() {

        let fbpagecontent;
        let instacontent;
        let instaconnected;

        if(this.state.allpost){
            instaconnected = (
                <Banner
                    title="Your shipping label is ready to print."
                    status="success"
                    action={{content: 'Print label'}}
                    onDismiss={() => {}}
                />
            );
        }


        if(this.state.pages!=''){
            fbpagecontent = (
                    <FacebookPageList openModal={this.openModal} pages={this.state.pages} instastatus={instaconnected}/>
            )
        }

        if(this.state.getpost){

            instacontent = (
                <Instagram allpost={this.state.allpost} />
            )
        }


        return <Page><div className='fb_page_model'>{fbpagecontent}{instacontent}</div></Page>;
    }
}


export default Facebook;
