    return (
        <div style={{height: '50px'}}>

            <div className={pageclass}>
                <Button onClick={handleChange}>Open</Button>
            </div>
            <Modal
                open={active}
                title="Business Pages"
                onScrolledToBottom={handleScrollBottom}
                className='my-own-class'
            >
                {items.map((value, index) => {

                    return  <Modal.Section key={index} className='my-own-class'>
                        <ResourceItem
                            id={value.id}
                            name={value.category}
                            onClick={this.handlepage.bind(value.id)}
                            accessibilityLabel={`View details for ${value.category}`}
                        >
                            <h3>
                                <TextStyle variation="strong">{value.name}</TextStyle>
                            </h3>
                            <div>{value.category}</div>
                        </ResourceItem>
                    </Modal.Section>

                })}

            </Modal>
        </div>
    );
}

