class Instasettings extends React.Component {
    constructor(props) {
        super(props);

        this.handleTabChange = this.handleTabChange.bind(this);

        this.state = {
            selectedTab: 0,
        };
    }

    handleTabChange(selectedTab) {
        this.setState({selectedTab});
    }


    render() {


        const {selectedTab} = this.state;

