import React, { useCallback, useState } from 'react';
import {Page,Button,Modal, Card, ResourceItem,Scrollable, TextStyle} from '@shopify/polaris';



function FacebookPageList(props) {

    const items = props.pages;

    const [active, setActive] = useState(true);

    const handleChange = useCallback(() => setActive(!active), [active]);

    const handleScrollBottom = useCallback(() => alert('Scrolled to bottom'), []);

    return (
        <div style={{height: '500px'}}>
            <Button className="fb_page_model_button" onClick={handleChange}>Open</Button>
            <Modal
                open={active}
                title="Scrollable content"
                onClose={handleChange}
                onScrolledToBottom={handleScrollBottom}
            >
                {items.map((value, index) => {

                    return  <Modal.Section key={index}>
                        <ResourceItem
                            id={value.id}
                            name={value.category}
                            url= {`instagram?page_id=${value.id}&access_token=${value.access_token}`}
                            accessibilityLabel={`View details for ${value.category}`}
                        >
                            <h3>
                                <TextStyle variation="strong">{value.name}</TextStyle>
                            </h3>
                            <div>{value.category}</div>
                        </ResourceItem>
                    </Modal.Section>

                })}

            </Modal>
        </div>
    );
}

