import React, { useCallback, useState } from 'react';
import {Page,Button,Modal, Card, ResourceItem,Scrollable, TextContainer} from '@shopify/polaris';



function FacebookPageList(props) {

    const items = props.pages;

    const [active, setActive] = useState(true);

    const handleChange = useCallback(() => setActive(!active), [active]);

    const handleScrollBottom = useCallback(() => alert('Scrolled to bottom'), []);

    return (
        <div style={{height: '500px'}}>
            <Button onClick={handleChange}>Open</Button>
            <Modal
                open={active}
                title="Scrollable content"
                onClose={handleChange}
                onScrolledToBottom={handleScrollBottom}
            >
                {items.map((value, index) => {

                    {/*<Modal.Section key={index}>*/}
                        {/*<TextContainer>*/}
                            {/*<p>Item #{index}</p>*/}
                        {/*</TextContainer>*/}
                    {/*</Modal.Section>*/}

                })}

            </Modal>
        </div>
    );
}

function ResourceListWithSelectionExample(props) {

    const resourceName = {
        singular: 'Pages',
        plural: 'Pages',
    };

    const items = props.pages;


    return (
        <Card>
            <ResourceList
                resourceName={resourceName}
                items={items}
                renderItem={renderItem}
            />
        </Card>
    );

    function renderItem(item) {
        const {id, category, name,access_token} = item;


        return (
            <ResourceItem
                id={id}
                name={category}
                url= {`instagram?page_id=${id}&access_token=${access_token}`}
                accessibilityLabel={`View details for ${category}`}
            >
                <h3>
                    <TextStyle variation="strong">{name}</TextStyle>
                </h3>
                <div>{category}</div>
            </ResourceItem>
        );
    }
}

class Facebook extends React.Component {


    constructor(props) {
        super(props);

        this.state = {
            pages: props.alldata.data,
        };

    }


    render() {


        let fbpagecontent;

        if(this.state.pages!=''){
            fbpagecontent = (
                    <FacebookPageList pages={this.state.pages} />
            )
        }

        return <Page>{fbpagecontent}</Page>;
    }
}


export default Facebook;
