import React from 'react';
import {Button, Heading, Form, FormLayout,SettingToggle} from '@shopify/polaris';

import "@shopify/polaris/styles.css";



class Acceptinformation extends React.Component {
    constructor(props) {
        super(props);


        this.state = {
            info_like : false,
            info_like_status : 'Enable',
            info_comment : false,
            info_comment_status : 'Enable',
            info_description : false,
            info_description_status : 'Enable',
            social_sharing : false,
            social_sharing_button_name : 'Enable',
        }


        this.handleInfoLike = this.handleInfoLike.bind(this);
        this.handleInfoComment = this.handleInfoComment.bind(this);
        this.handleInfoDescription = this.handleInfoDescription.bind(this);
        this.handleToggleSocial = this.handleToggleSocial.bind(this);

    }


    handleInfoLike(){

        if(this.state.info_like == false) {
            this.setState({
                info_like : true,
                info_like_status : 'Disable'
            })
        }else{
            this.setState({
                info_like : false,
                info_like_status : 'Enable'
            })
        }

    }

    handleInfoComment(){

        if(this.state.info_comment == false) {
            this.setState({
                info_comment : true,
                info_comment_status : 'Disable'
            })
        }else{
            this.setState({
                info_comment : false,
                info_comment_status : 'Enable'
            })
        }

    }

    handleInfoDescription(){

        if(this.state.info_description == false) {
            this.setState({
                info_description : true,
                info_description_status : 'Disable'
            })
        }else{
            this.setState({
                info_description : false,
                info_description_status : 'Enable'
            })
        }

    }


    handlePopupLike(){

        if(this.state.info_like == false) {
            this.setState({
                info_like : true,
                info_like_status : 'Disable'
            })
        }else{
            this.setState({
                info_like : false,
                info_like_status : 'Enable'
            })
        }

    }

    handlePopupComment(){

        if(this.state.info_comment == false) {
            this.setState({
                info_comment : true,
                info_comment_status : 'Disable'
            })
        }else{
            this.setState({
                info_comment : false,
                info_comment_status : 'Enable'
            })
        }

    }

    handlePopupDescription(){

        if(this.state.info_description == false) {
            this.setState({
                info_description : true,
                info_description_status : 'Disable'
            })
        }else{
            this.setState({
                info_description : false,
                info_description_status : 'Enable'
            })
        }

    }



    render() {


        return <div>
            <Form
                // onSubmit={handleSubmit}
            >
                <FormLayout>
                    <Heading>Information</Heading>
                    <FormLayout.Group>

                        <SettingToggle
                            action={{
                                content: this.state.info_like_status,
                                onAction: this.handleInfoLike,
                            }}
                            enabled={this.state.info_like}
                            >Like Count</SettingToggle>

                        <SettingToggle
                            action={{
                                content: this.state.info_comment_status,
                                onAction: this.handleInfoComment,
                            }}
                            enabled={this.state.info_comment}>Comment Count</SettingToggle>
                        <SettingToggle
                            action={{
                                content: this.state.info_description_status,
                                onAction: this.handleInfoDescription,
                            }}
                            enabled={this.state.info_description}>Description</SettingToggle>

                    </FormLayout.Group>

                    <Heading>Popup</Heading>

                    <FormLayout.Group>
                        <SettingToggle
                            action={{
                                content: this.state.social_sharing_button_name,
                                onAction: this.handlePopupLike,
                            }}
                            enabled={this.state.social_sharing}>Like Count</SettingToggle>
                        <SettingToggle
                            action={{
                                content: this.state.social_sharing_button_name,
                                onAction: this.handleToggleSocial,
                            }}
                            enabled={this.state.social_sharing}>Comment Count</SettingToggle>
                        <SettingToggle
                            action={{
                                content: this.state.social_sharing_button_name,
                                onAction: this.handleToggleSocial,
                            }}
                            enabled={this.state.social_sharing}>Description</SettingToggle>

                    </FormLayout.Group>

                    <Button submit>Submit</Button>
                </FormLayout>
            </Form>
        </div>;
    }


}

export default Acceptinformation;
