        const { color, popoverActive } = this.state;

        const rgbaColor = rgbString(hsbToRgb(color));

        const activator = (
            <Button onClick={this.handlePopoverOpen}>
                <Stack alignment="center" spacing="tight">
                    <div
                        style={{
                            height: "2rem",
                            width: "2rem",
                            borderRadius: "0.3rem",
                            background: rgbaColor
                        }}
                    />
                    <span>Primary color</span>
                </Stack>
            </Button>
        );


        // const handleSubmit = useCallback((_event) => {
        //     setEmail('sdd');
        //     setNewsletter(false);
        // }, []);
        //
        // const handleNewsLetterChange = useCallback(
        //     (value) => setNewsletter(value),
        //     [],
        // );
        // const [selected, setSelected] = useState('grid');
        // const [selected1, setSelected1] = useState('zoom_out');
        // const handleSelectChange = useCallback((value) => setSelected(value), []);
        // const handleSelectChange1 = useCallback((value) => setSelected1(value), []);
        //
        // const handleEmailChange = useCallback((value) => setEmail(value), []);

        const options = [
            {label: 'Grid', value: 'grid'},
            {label: 'List', value: 'list'}
        ];

        const options1 = [
            {label: 'Zoom Out', value: 'zoom_out'},
            {label: 'Zoom In', value: 'zoom_in'}
        ];

        // const [active, setActive] = useState(false);
        //
        // const handleToggle = useCallback(() => setActive((active) => !active), []);
        //
        // const contentStatus = active ? 'Disable' : 'Enable';
        //
        //
        // const [columnvalue, setcolumnvalue] = useState('1');
        //
        // const handleChangecolumn = useCallback((newValue) => setcolumnvalue(newValue), []);
        //
        // const [rowvalue, setrowvalue] = useState('1');
        //
        // const handleChangerow = useCallback((newValue) => setrowvalue(newValue), []);



        // const [textwidthFieldValue, setTextFieldWidthValue] = useState('10.6');
        // const [selectwidthValue, setBorderwidthValue] = useState('kg');

        // const handleTextFieldwidthChange = useCallback(
        //     (value) => setTextFieldWidthValue(value),
        //     [],
        // );
        //
        // const handleSelectwidthChange = useCallback((value) => setBorderwidthValue(value), []);


        return <div>
            <Form
                // onSubmit={handleSubmit}
            >
                <FormLayout>
                    <FormLayout.Group>

                        <Select
                            label="Widget Type"
                            options={options}
                            onChange={this.handleSelectChange}
                            value={this.state.checkwidget}
                        />

                        <Select
                            label="Hover Effect"
                            options={options1}
                            onChange={this.handleSelectHoveeffect}
                            value={this.state.checkhover}
                        />

                        <SettingToggle
                            action={{
                                content: this.state.display_border_button_name,
                                onAction: this.handleToggle,
                            }}
                            enabled={this.state.display_border}
                        />


                    </FormLayout.Group>
                    {/*<FormLayout.Group>*/}
                        {/*<TextField type="number" label="Layout Columns"*/}
                                   {/*// value={columnvalue} onChange={handleChangecolumn}*/}
                        {/*/>*/}
                        {/*<TextField type="number" label="Layout Rows"*/}
                                   {/*// value={rowvalue} onChange={handleChangerow}*/}
                        {/*/>*/}
                        {/*<SettingToggle*/}
                            {/*action={{*/}
                                {/*// content: contentStatus,*/}
                                {/*// onAction: handleToggle,*/}
                            {/*}}*/}
                            {/*// enabled={active}*/}
                        {/*/>*/}
                    {/*</FormLayout.Group>*/}
                    {/*<FormLayout.Group>*/}
                        {/*<TextField*/}
                            {/*label="Border Width"*/}
                            {/*type="number"*/}
                            {/*// value={textwidthFieldValue}*/}
                            {/*// onChange={handleTextFieldwidthChange}*/}
                            {/*connectedRight={*/}
                                {/*<Select*/}
                                    {/*// value={selectwidthValue}*/}
                                    {/*label="Pixel Unit"*/}
                                    {/*// onChange={handleSelectwidthChange}*/}
                                    {/*labelHidden*/}
                                    {/*options={['PX']}*/}
                                {/*/>*/}
                            {/*}*/}
                        {/*/>*/}

                        {/*<TextField label="Widget Heading"*/}

                                   {/*// value={columnvalue} onChange={handleChangecolumn}*/}
                        {/*/>*/}
                        {/*<TextField label="Border Color" type="color"*/}
                                   {/*// value={columnvalue} onChange={handleChangecolumn}*/}
                        {/*/>*/}


                    {/*</FormLayout.Group>*/}
                    <FormLayout.Group>
                        <Popover
                            active={popoverActive}
                            activator={activator}
                            onClose={this.handlePopoverClose}
                        >
                            <Popover.Section>
                                <ColorPicker
                                    onChange={this.handleColorChange}
                                    color={color}
                                    allowAlpha
                                />
                            </Popover.Section>
                            <Popover.Section>
                                <TextField value={rgbaColor} onChange={this.handleRgbChange} />
                            </Popover.Section>
                        </Popover>
                    </FormLayout.Group>

                    <Button submit>Submit</Button>
                </FormLayout>
            </Form>
        </div>;
    }


}

export default Postsettings;
