import React, {useCallback, useState} from 'react';
import {AppProvider,Button, Checkbox, Form, FormLayout,SettingToggle,TextStyle, TextField,Select,Card,Page,Popover,ColorPicker,Stack,hsbToRgb,rgbToHsb,rgbString} from '@shopify/polaris';
import { autobind } from "@shopify/javascript-utilities/decorators";
import "@shopify/polaris/styles.css";



class Postsettings extends React.Component {
    constructor(props) {
        super(props);


    }

    state = {
        color: {
            hue: 300,
            brightness: 1,
            saturation: 0.7,
            alpha: 0.8
        },
        popoverActive: false
    };

    handleColorChange(color) {
        this.setState({ color });
    }


    handleRgbChange(value) {
        const rgbValues = value.replace(/[^\d*.?\d*,]/g, "").split(",");
        const color = rgbToHsb({
            red: rgbValues[0],
            green: rgbValues[1],
            blue: rgbValues[2],
            alpha: rgbValues[3]
        });
        this.setState({ color });
    }


    handlePopoverClose() {
        this.setState({ popoverActive: false });
    }


    handlePopoverOpen() {
        this.setState({ popoverActive: true });
    }


    render() {


        const { color, popoverActive } = this.state;

        const rgbaColor = rgbString(hsbToRgb(color));

        const activator = (
            <Button onClick={this.handlePopoverOpen}>
                <Stack alignment="center" spacing="tight">
                    <div
                        style={{
                            height: "2rem",
                            width: "2rem",
                            borderRadius: "0.3rem",
                            background: rgbaColor
                        }}
                    />
                    <span>Primary color</span>
                </Stack>
            </Button>
        );


