import React from 'react';
import axios from 'axios';
import request from 'superagent'


class Index extends React.Component {

    // state = {
    //     access_token : ''
    // }


    componentDidMount() {

        fetch("http://localhost:5000/abcd",this.getUrlParameter('code'))
            .then(res => res.json())
            .then(
                (result) => {
                    console.log(result);
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )

    }

    getUrlParameter(sParam) {
        var sPageURL = window.location.search.substring(1),
            sURLVariables = sPageURL.split('&'),
            sParameterName,
            i;

        for (i = 0; i < sURLVariables.length; i++) {
            sParameterName = sURLVariables[i].split('=');

            if (sParameterName[0] === sParam) {
                return sParameterName[1] === undefined ? true : decodeURIComponent(sParameterName[1]);
            }
        }
    };

    render() {
        return <a href="https://api.instagram.com/oauth/authorize?client_id=832162937288585&redirect_uri=https://0a1192c7.ngrok.io/&scope=user_profile,user_media&response_type=code"><span>Login with Instagram</span></a>;
    }
}



export default Index;
