        let fbContent;

        if(this.state.isFBLoggedIn){
            fbContent = (
                <div style={{width:'400px',margin:'auto',background:'#f4f4f4',padding:'20px'}}>
                    <Link onclick={this.logoutFacebook}>Logout</Link>
