import React from 'react';
import axios from 'axios';
import request from 'superagent'


class Index extends React.Component {

    // state = {
    //     access_token : ''
    // }


    componentDidMount() {

        // axios.defaults.headers.common['client_id'] = '832162937288585';
        // axios.defaults.headers.common['client_secret'] = '4441a79ce3869c0d47c5679a8b1413fb';
        // axios.defaults.headers.common['grant_type'] = 'authorization_code';
        // axios.defaults.headers.common['redirect_uri'] = 'https://bb5c8bc3.ngrok.io/';
        // axios.defaults.headers.common['code'] = this.getUrlParameter('code');
        // axios.get('https://api.instagram.com/oauth/access_token')
        //     .then(function (response) {
        //         // handle success
        //         console.log(response);
        //     })
        //     .catch(function (error) {
        //         // handle error
        //         console.log(error);
        //     })
        //     .finally(function () {
        //         // always executed
        //     });

        // const accessTokenRequestUrl = 'https://api.instagram.com/oauth/access_token';
        // const accessTokenPayload = {
        //     client_id:'832162937288585',
        //     client_secret:'4441a79ce3869c0d47c5679a8b1413fb',
        //     grant_type:'authorization_code',
        //     redirect_uri:'https://a4ee52a2.ngrok.io',
        //     code:this.getUrlParameter('code')
        // };
        //
        // if(this.getUrlParameter('code')!=''){
        //     request
        //         .post(accessTokenRequestUrl,accessTokenPayload)
        //         .then((accessTokenResponse) => {
        //             console.log(accessTokenResponse);
        //         }).catch(err => log(err))
        // }


        fetch("http://localhost:5000/abcd",this.getUrlParameter('code'))
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        items: result.items
                    });
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )

    }

    getUrlParameter(sParam) {
        var sPageURL = window.location.search.substring(1),
            sURLVariables = sPageURL.split('&'),
            sParameterName,
            i;

        for (i = 0; i < sURLVariables.length; i++) {
            sParameterName = sURLVariables[i].split('=');

            if (sParameterName[0] === sParam) {
                return sParameterName[1] === undefined ? true : decodeURIComponent(sParameterName[1]);
            }
        }
    };

    render() {
        return <a href="https://api.instagram.com/oauth/authorize?client_id=832162937288585&redirect_uri=https://0a1192c7.ngrok.io/&scope=user_profile,user_media&response_type=code"><span>Login with Instagram</span></a>;
    }
}



export default Index;
