import React from 'react';
import axios from 'axios';


class Index extends React.Component {

    state = {
        access_token : ''
    }

    componentDidMount() {
        var urlParams = new URLSearchParams(window.location.search);

        console.log(window.location.href);

        this.setState({ access_token: urlParams.has('post') });
    }

    render() {
        return <a href="https://api.instagram.com/oauth/authorize?client_id=832162937288585&redirect_uri=https://bb5c8bc3.ngrok.io/&scope=user_profile,user_media&response_type=code"><span>Login with Instagram</span></a>;
    }
}

export default Index;
