import axios from 'axios';
import request from 'superagent'
import FacebookLogin from 'react-facebook-login';
import {Link} from '@shopify/polaris';
import FacebookPage from '../pages/facebookpages';

class Index extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            isFBLoggedIn : false,
            userID:'',
            name:'',
            email:'',
            picture:'',
            allpages:''
        };

        // This binding is necessary to make `this` work in the callback
        this.logoutFacebook = this.logoutFacebook.bind(this);
        this.abcd = this.abcd.bind(this);

    }



    logoutFacebook(){
        console.log('111');
        this.setState({
            isFBLoggedIn : false
        })
    }

    abcd(response,response1) {
        console.log(response.data);
        this.setState({
            isFBLoggedIn : true,
            userID:response1.userID,
            name:response1.name,
            email:response1.email,
            picture:response1.picture.data.url,
            allpages : response.data
        })
    }


       responseFacebook = (response) => {

           this.setState({
               isFBLoggedIn : true,
               userID:response.userID,
               name:response.name,
               email:response.email,
               picture:response.picture.data.url,
           });
           var self=this;

           axios.post('http://localhost:5000/abcd/',response)
               .then(function (response1) {
                   // var result1 = JSON.stringify(response1);
